INSERT INTO `video`
VALUES (102, 1, '美112食频道 - 热门美食视频大全 - 抖音_5.mp4',
        'http://sg89iek10.hn-bkt.clouddn.com/e0e0cdd5-4079-4879-b3c1-b4b6aa3a62a3.mp4',
